Dim leadImage As RasterImage = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg")
' Prepare the command Dim command As New MaskConvolutionCommand
' Apply 3D emboss effect on the image, with 0 degrees as the angle of apparent light, ' depth of 1.50, and height of 3 command.Angle = 0 command.Depth = 150 command.Height = 3 command.Type = MaskConvolutionCommandType.Emboss command.Run(leadImage) codecs.Save(leadImage, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24)
RasterCodecs.Shutdown() End Sub public void MaskConvolutionCommandExample() { // Load an image RasterCodecs.Startup(); RasterCodecs codecs = new RasterCodecs(); codecs.ThrowExceptionsOnInvalidImages = true;
RasterImage image = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg");
