// Apply 3D emboss effect on the image, with 0 degrees as the angle of apparent light, // depth of 1.50, and height of 3 command.Angle = 0; command.Depth = 150; command.Height = 3; command.Type = MaskConvolutionCommandType.Emboss; command.Run(image); codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);
