public void BinaryFilterConstructorExample_S1()
{
   // Load an image
   RasterCodecs.Startup();
   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;

   RasterImage image = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg");

   // Prepare the command
   int [] nMatrix = new int[9];
   nMatrix[0] = 0;
   nMatrix[1] = 0;
   nMatrix[2] = 0;
   nMatrix[3] = 0;
   nMatrix[4] = 0;
   nMatrix[5] = 0;
   nMatrix[6] = 0;
   nMatrix[7] = 0;
   nMatrix[8] = 0;

   BinaryFilterCommand command = new BinaryFilterCommand();
   command.Matrix = nMatrix;
   command.Maximum = true;
   // Dilate black objects.
   command.Run(image);
   codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);

   RasterCodecs.Shutdown();
}

public void BinaryFilterConstructorExample_S1(RasterImage image, Stream outStream)
{
   // Prepare the command
   int[] nMatrix = new int[9];
   nMatrix[0] = 0;
   nMatrix[1] = 0;
   nMatrix[2] = 0;
   nMatrix[3] = 0;
   nMatrix[4] = 0;
   nMatrix[5] = 0;
   nMatrix[6] = 0;
   nMatrix[7] = 0;
   nMatrix[8] = 0;
   BinaryFilterCommand command = new BinaryFilterCommand();
   command.Matrix = nMatrix;
   command.Maximum = true;
   // Dilate black objects.
   command.Run(image);
   // Save result image
   RasterCodecs codecs = new RasterCodecs();
   codecs.Save(image, outStream, RasterImageFormat.Jpeg, 24);
   image.Dispose();
}

public void BinaryFilterConstructorExample_S2()
{
   // Load an image
   RasterCodecs.Startup();
   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;

   RasterImage image = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg");

   // Prepare the command
   int [] nMatrix = new int[9];
   nMatrix[0] = 0;
   nMatrix[1] = 0;
   nMatrix[2] = 0;
   nMatrix[3] = 0;
   nMatrix[4] = 0;
   nMatrix[5] = 0;
   nMatrix[6] = 0;
   nMatrix[7] = 0;
   nMatrix[8] = 0;

   BinaryFilterCommand command = new BinaryFilterCommand(true, nMatrix);
   // Dilate black objects.
   command.Run(image);
   codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);
   MessageBox.Show("The dimension property has the value = " + command.Dimension.ToString());

   RasterCodecs.Shutdown();
}

public void BinaryFilterConstructorExample_S2(RasterImage image, Stream outStream)
{
   // Prepare the command
   int[] nMatrix = new int[9];
   nMatrix[0] = 0;
   nMatrix[1] = 0;
   nMatrix[2] = 0;
   nMatrix[3] = 0;
   nMatrix[4] = 0;
   nMatrix[5] = 0;
   nMatrix[6] = 0;
   nMatrix[7] = 0;
   nMatrix[8] = 0;
   BinaryFilterCommand command = new BinaryFilterCommand(true, nMatrix);
   // Dilate black objects.
   command.Run(image);
   // Save result image
   RasterCodecs codecs = new RasterCodecs();
   codecs.Save(image, outStream, RasterImageFormat.Jpeg, 24);
   image.Dispose();
   Debug.WriteLine("The dimension property has the value = " + command.Dimension.ToString());
}
