' Prepare the command Dim command As AgingCommand = New AgingCommand()
command.HorizontalScratchCount = 10 command.VerticalScratchCount = 2 command.MaximumScratchLength = 50 command.DustDensity = 2 command.PitsDensity = 5 command.MaximumPitSize = 6 command.ScratchColor = New RasterColor(255, 255, 0) command.DustColor = New RasterColor(0, 0, 0) command.PitsColor = New RasterColor(0, 0, 255) command.Flags = AgingCommandFlags.AddVerticalScratch Or AgingCommandFlags.AddPits Or AgingCommandFlags.ScratchInverse Or AgingCommandFlags.PitsColor
'Apply aging effect. command.Run(leadImage) codecs.Save(leadImage, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24)
RasterCodecs.Shutdown() End Sub public void AgingCommandExample() { // Load an image RasterCodecs.Startup(); RasterCodecs codecs = new RasterCodecs(); codecs.ThrowExceptionsOnInvalidImages = true;
RasterImage image = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg");
// Prepare the command AgingCommand command = new AgingCommand(); command.HorizontalScratchCount = 10; command.VerticalScratchCount = 2; command.MaximumScratchLength = 50; command.DustDensity = 2; command.PitsDensity = 5; command.MaximumPitSize = 6; command.ScratchColor = new RasterColor(255, 255, 0); command.DustColor = new RasterColor(0, 0, 0); command.PitsColor = new RasterColor(0, 0, 255); command.Flags = AgingCommandFlags.AddVerticalScratch | AgingCommandFlags.AddPits | AgingCommandFlags.ScratchInverse | AgingCommandFlags.PitsColor;
//Apply aging effect. command.Run(image); codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);
RasterCodecs.Shutdown(); }
