' Prepare the command Dim command As MosaicTilesCommand = New MosaicTilesCommand command.BorderColor = New RasterColor(0, 0, 0) command.TilesColor = New RasterColor(255, 255, 255) command.TileWidth = 50 command.TileHeight = 50 command.Opacity = 50 command.ShadowThreshold = 50 command.ShadowAngle = ShadowCommandAngle.East command.PenWidth = 7 command.Flags = MosaicTilesCommandFlags.Cartesian Or _ MosaicTilesCommandFlags.ShadowGray command.Run(leadImage) codecs.Save(leadImage, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24)
RasterCodecs.Shutdown() End Sub public void MosaicTilesCommandExample() { // Load an image RasterCodecs.Startup(); RasterCodecs codecs = new RasterCodecs(); codecs.ThrowExceptionsOnInvalidImages = true;
RasterImage image = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg");
// Prepare the command MosaicTilesCommand command = new MosaicTilesCommand(); command.BorderColor = new RasterColor(0,0,0); command.TilesColor = new RasterColor(255,255,255); command.TileWidth = 50; command.TileHeight = 50; command.Opacity = 50; command.ShadowThreshold = 50; command.ShadowAngle = ShadowCommandAngle.East; command.PenWidth = 7; command.Flags = MosaicTilesCommandFlags.Cartesian | MosaicTilesCommandFlags.ShadowGray ; command.Run(image); codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);
RasterCodecs.Shutdown(); }
