' Prepare the command Dim command As GlowCommand = New GlowCommand command.Dimension = 5 command.Brightness = 3 command.Threshold = 0 ' Apply glow effect on the image. command.Run(leadImage) codecs.Save(leadImage, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24)
RasterCodecs.Shutdown() End Sub public void GlowCommandExample() { // Load an image RasterCodecs.Startup(); RasterCodecs codecs = new RasterCodecs(); codecs.ThrowExceptionsOnInvalidImages = true;
RasterImage image = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg");
// Prepare the command GlowCommand command = new GlowCommand(); command.Dimension = 5; command.Brightness = 3; command.Threshold = 0; // Apply glow effect on the image. command.Run(image); codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);
RasterCodecs.Shutdown(); }
