' Prepare the command Dim command As FreePlaneBendCommand = New FreePlaneBendCommand Dim UserPoint() As LeadPoint ReDim UserPoint(3)
Dim Curve() As Integer ReDim Curve(12)
'Apply the filter UserPoint(0).X = 0 UserPoint(0).Y = 0 UserPoint(1).X = 3 UserPoint(1).Y = 10 UserPoint(2).X = 9 UserPoint(2).Y = -10 UserPoint(3).X = 12 UserPoint(3).Y = 0
EffectsUtilities.GetCurvePoints(Curve, UserPoint, CurvePointsType.Linear) command.Flags = FreePlaneBendCommandFlags.Color Or FreePlaneBendCommandFlags.Vertical command.Scale = 200 command.FillColor = New RasterColor(255, 0, 0) command.Curve = Curve command.Run(leadImage) codecs.Save(leadImage, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24)
