//Apply the filter UserPoint[0].X = 0; UserPoint[0].Y = 0; UserPoint[1].X = 3; UserPoint[1].Y = 10; UserPoint[2].X = 9; UserPoint[2].Y = -10; UserPoint[3].X = 12; UserPoint[3].Y = 0;
EffectsUtilities.GetCurvePoints(Curve, UserPoint, CurvePointsType.Linear); for(int i = 0; i < Curve.Length; i++) Curve[i] /= 50; command.Flags = FreePlaneBendCommandFlags.Color | FreePlaneBendCommandFlags.Vertical; command.Scale = 200; command.FillColor = new RasterColor(255, 0, 0); command.Curve = Curve; command.Run(image); codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);
RasterCodecs.Shutdown(); }
