public void PlaneConstructorExample()
{
   // Load an image
   RasterCodecs.Startup();
   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;

   RasterImage image = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg");

   // Prepare the command
   PlaneCommand command = new PlaneCommand(new LeadPoint(image.Width / 2, image.Height / 2), 0, image.Height, image.Width / 2, -1, 0, 25, 0, 100, 20000, new RasterColor(255, 255, 255), new RasterColor(0, 0, 0), PlaneCommandFlags.Right | PlaneCommandFlags.Left | PlaneCommandFlags.Color);
   command.Run(image);
   codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);

   RasterCodecs.Shutdown();
}

members.member:
Draws a star on the image. This command is available in the Raster Pro and above toolkits.
If the image has a region, the effect will be applied on the region only.
This command supports 12- and 16-bit grayscale and 48- and 64-bit color images. Support for 12- and 16-bit grayscale and 48- and 64-bit color images is available only in the Document/Medical toolkits.
This command does not support signed data images.
This command does not support 32-bit grayscale images.
For more information, refer to Correcting Colors.
Run the on an image. Public Sub DrawStarCommandExample() RasterCodecs.Startup() Dim codecs As New RasterCodecs() codecs.ThrowExceptionsOnInvalidImages = True
Dim leadImage As RasterImage = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg")
' Prepare the command Dim command As DrawStarCommand = New DrawStarCommand command.CenterPoint = New LeadPoint(leadImage.Width \ 2, leadImage.Height \ 2) command.Spoke = 5 command.StarWidth = leadImage.Width \ 2 command.StarHeight = leadImage.Height \ 2 command.HoleSize = 50 command.Phase = 0 command.Angle = 0 command.DistanceOpacity = 0 command.SpokeDivision = 0 command.AngleOpacity = 0 command.BorderOpacity = 0 command.LowerColorFill = New RasterColor(255, 0, 0) command.UpperColorFill = New RasterColor(0, 0, 255) command.Opacity = 100 command.Flags = DrawStarCommandFlags.Inside
command.Run(leadImage) codecs.Save(leadImage, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24)
RasterCodecs.Shutdown() End Sub public void DrawStarCommandExample() { // Load an image RasterCodecs.Startup(); RasterCodecs codecs = new RasterCodecs(); codecs.ThrowExceptionsOnInvalidImages = true;
RasterImage image = codecs.Load(LeadtoolsExamples.Common.ImagesPath.Path + "Master.jpg");
// Prepare the command DrawStarCommand command = new DrawStarCommand();
command.CenterPoint = new LeadPoint(image.Width/2, image.Height/2); command.Spoke = 5; command.StarWidth = image.Width/2; command.StarHeight = image.Height/2; command.HoleSize = 50; command.Phase = 0; command.Angle = 0; command.DistanceOpacity = 0; command.SpokeDivision = 0; command.AngleOpacity = 0; command.BorderOpacity = 0; command.LowerColorFill = new RasterColor(255, 0, 0); command.UpperColorFill = new RasterColor(0, 0, 255); command.Opacity = 100; command.Flags = DrawStarCommandFlags.Inside;
command.Run(image); codecs.Save(image, LeadtoolsExamples.Common.ImagesPath.Path + "Result.jpg", RasterImageFormat.Jpeg, 24);
RasterCodecs.Shutdown(); }
