private void SaveStringExample()
{
   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;

   // Load a JPEG 2000 image
   Jpeg2000Engine engine = new Jpeg2000Engine();
   RasterImage image = engine.Load(codecs, LeadtoolsExamples.Common.ImagesPath.Path + "image1.jp2", 0, CodecsLoadByteOrder.BgrOrGray);

   List<XmlBox> xmlBoxes = engine.GetBoxes<XmlBox>(Jpeg2000FileFormat.LeadJp2);
   ResolutionBox resBox = (ResolutionBox)engine.GetBox(Jpeg2000FileFormat.LeadJp2, Jpeg2000BoxType.ResolutionBox);

   engine.ResetEngineBoxes();
   //Set the JPX engine's XML box
   engine.SetBoxes(Jpeg2000FileFormat.LeadJpx, xmlBoxes);
   engine.SetBox(Jpeg2000FileFormat.LeadJpx, resBox);
   //Save the image in JPX file format
   engine.Save(codecs, LeadtoolsExamples.Common.ImagesPath.Path + "Test.jpx", image, Jpeg2000FileFormat.LeadJpx, 24, 5);
   //Append Intellectual Property Rights box
   String copyRights = ("Copyright (c) 1991-2008 by LEAD Technologies, Inc. All Rights Reserved.");
   IprBox ipr = new IprBox();
   ipr.Data = Encoding.ASCII.GetBytes(copyRights);
   engine.AppendBox(LeadtoolsExamples.Common.ImagesPath.Path + "Test.jpx", ipr);
   // Clean up
   image.Dispose();
   codecs.Dispose();
}

private void SaveStreamExample()
{
   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;

   FileStream fs = File.OpenRead(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jp2");

   // Load a JPEG 2000 image
   Jpeg2000Engine engine = new Jpeg2000Engine();
   RasterImage image = engine.Load(codecs, fs, 0, CodecsLoadByteOrder.BgrOrGray);

   List<XmlBox> xmlBoxes = engine.GetBoxes<XmlBox>(Jpeg2000FileFormat.LeadJp2);
   ResolutionBox resBox = (ResolutionBox)engine.GetBox(Jpeg2000FileFormat.LeadJp2, Jpeg2000BoxType.ResolutionBox);

   engine.ResetEngineBoxes();
   //Set the JPX engine's XML box
   engine.SetBoxes(Jpeg2000FileFormat.LeadJpx, xmlBoxes);
   engine.SetBox(Jpeg2000FileFormat.LeadJpx, resBox);
   fs.Close();
   fs = File.Create(LeadtoolsExamples.Common.ImagesPath.Path + "Test.jpx");
   //Save the image in JPX file format
   engine.Save(codecs, fs, image, Jpeg2000FileFormat.LeadJpx, 24, 5);
   //Append Intellectual Property Rights box
   String copyRights = ("Copyright (c) 1991-2009 by LEAD Technologies, Inc. All Rights Reserved.");
   IprBox ipr = new IprBox();
   ipr.Data = Encoding.ASCII.GetBytes(copyRights);
   engine.AppendBox(fs, ipr);

   // Clean up
   fs.Close();
   image.Dispose();
   codecs.Dispose();
}

private void SaveCompositeStringExample(byte [] xmlData)
{
   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;

   // Load a JPEG 2000 image
   Jpeg2000Engine engine = new Jpeg2000Engine();
   List<CompositeJpxImages> images = engine.LoadComposite(codecs, LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx", 0, CodecsLoadByteOrder.BgrOrGray);

   engine.ResetEngineBoxes();

   GtsoBox gtsoBox = (GtsoBox)(engine.ReadBox(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx",Jpeg2000BoxType.GtsoBox, 0));
   engine.SetBox(Jpeg2000FileFormat.LeadJpx, gtsoBox);
   //Save the image in JPX file format
   engine.SaveComposite(codecs, LeadtoolsExamples.Common.ImagesPath.Path + "Test.jpx", images, Jpeg2000FileFormat.LeadJpx, 24, 5);
   //Append an XML box
   XmlBox xmlBox = new XmlBox();
   xmlBox.Data = xmlData;
   List<XmlBox> xmlBoxes = new List<XmlBox>();
   xmlBoxes.Add(xmlBox);
   engine.AppendBoxes(LeadtoolsExamples.Common.ImagesPath.Path + "Test.jpx", xmlBoxes);
   // Clean up
   foreach (CompositeJpxImages image in images)
   {
      image.ColorImage.Dispose();
      image.OpacityImage.Dispose();
      image.PreOpacityImage.Dispose();
   }
   codecs.Dispose();
}

