private void ReadFramesStringExample()
{
   // Load a JPEG 2000 image
   Jpeg2000Engine engine = new Jpeg2000Engine();
   Jpeg2000FileInformation fileInfo = engine.GetFileInformation(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx");
   if (fileInfo.Frame.GetLength(0) < 2)
      return;

   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;
   List<int> frames = new List<int>();
   frames.Add(1);
   List<CompositeJpxImages> images = engine.ReadFrames(codecs,LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx",frames,0,CodecsLoadByteOrder.BgrOrGray);
   // Clean up
   foreach (CompositeJpxImages image in images)
   {
      image.ColorImage.Dispose();
      image.OpacityImage.Dispose();
      image.PreOpacityImage.Dispose();
   }
   codecs.Dispose();
}

private void ReadFramesStreamExample()
{
   FileStream fs = File.OpenRead(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx");
   // Load a JPEG 2000 image
   Jpeg2000Engine engine = new Jpeg2000Engine();
   Jpeg2000FileInformation fileInfo = engine.GetFileInformation(fs);
   if (fileInfo.Frame.GetLength(0) < 2)
      return;

   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;
   List<int> frames = new List<int>();
   frames.Add(1);
   List<CompositeJpxImages> images = engine.ReadFrames(codecs, fs, frames, 0, CodecsLoadByteOrder.BgrOrGray);
   // Clean up
   fs.Close();
   foreach (CompositeJpxImages image in images)
   {
      image.ColorImage.Dispose();
      image.OpacityImage.Dispose();
      image.PreOpacityImage.Dispose();
   }
   codecs.Dispose();
}

private void AppendFramesStringExample(String fileName, List<CompositeJpxImages>images)
{
   RasterCodecs codecs = new RasterCodecs();
   codecs.ThrowExceptionsOnInvalidImages = true;
   Jpeg2000Engine engine = new Jpeg2000Engine();
   engine.AppendFrames(codecs, fileName, images, 0, 5);
   // Clean up
   codecs.Dispose();
}

