private RasterNativeBuffer ExtractFramesNativeMemoryStringExample(List<int> frames)
{
   Jpeg2000Engine engine = new Jpeg2000Engine();

   Jpeg2000FileInformation fileInfo = engine.GetFileInformation(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx");

   for (int i = 0; i < frames.Count; i++)
      if (frames[i] >= fileInfo.Frame.GetLength(0))
         return RasterNativeBuffer.Empty;
   return engine.ExtractFramesNativeMemory(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx", frames);
}

private RasterNativeBuffer ExtractFramesNativeMemoryStreamExample(List<int> frames)
{
   FileStream fs = File.OpenRead(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx");

   Jpeg2000Engine engine = new Jpeg2000Engine();
   Jpeg2000FileInformation fileInfo = engine.GetFileInformation(fs);

   for (int i = 0; i < frames.Count; i++)
      if (frames[i] >= fileInfo.Frame.GetLength(0))
         return RasterNativeBuffer.Empty;
   RasterNativeBuffer buffer = engine.ExtractFramesNativeMemory(fs, frames);
   // Clean up
   fs.Close();
   return buffer;
}

