private void FragmentJpxFileExample()
{
   Jpeg2000Engine engine = new Jpeg2000Engine();

   Jpeg2000FileInformation fileInfo = engine.GetFileInformation(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx");

   if (1 > fileInfo.CodeStream.GetLength(0))
      return;

   List<int> frames = new List<int>();
   frames.Add(0);
   UuidUrl url = new UuidUrl();
   url.Flag = new byte[3];
   url.Flag[0] = 0;
   url.Flag[1] = 0;
   url.Flag[2] = 0;
   url.Version = 0;
   String loaction = "c:\\codestream.bin";
   char[] data = loaction.ToCharArray();
   url.Location = new byte[data.GetLength(0)];
   data.CopyTo(url.Location, 0);

   List<UuidUrl> urls= new List<UuidUrl> ();
   urls.Add(url);
   Fragment fragment = new Fragment();
   fragment.CodeStreamIndex = 0;
   fragment.Offset = 0;
   fragment.UrlIndex = 0;
   List<Fragment> fragments = new List<Fragment> ();
   fragments.Add(fragment);
   engine.FragmentJpxFile(LeadtoolsExamples.Common.ImagesPath.Path + "image1.jpx", LeadtoolsExamples.Common.ImagesPath.Path + "Test.jpx", urls, fragments);
}

private void AppendGmlDataStringExample(String inputFile, String outputFile, GmlElement newData)
{
   Jpeg2000Engine engine = new Jpeg2000Engine();
   //Read GML data in the input file
   GmlData gml = engine.ReadGmlData(inputFile);
   //Add the new gml element data
   gml.Data.Add(newData);
   List<int> frames = new List<int>();
   //extract all inputfile frames
   Jpeg2000FileInformation fileInfo = engine.GetFileInformation(inputFile);
   for (int i = 0; i < fileInfo.Frame.GetLength(0); i++)
      frames.Add(i);
   engine.ExtractFrames(inputFile, outputFile, frames);
   //add all GML data
   engine.AppendGmlData(outputFile, gml);
}

private void AppendGmlDataStreamExample(FileStream inputStream, FileStream outputStream, GmlElement newData)
{
   Jpeg2000Engine engine = new Jpeg2000Engine();
   //Read GML data in the input file
   GmlData gml = engine.ReadGmlData(inputStream);
   //Add the new gml element data
   gml.Data.Add(newData);
   List<int> frames = new List<int>();
   //extract all inputfile frames
   Jpeg2000FileInformation fileInfo = engine.GetFileInformation(inputStream);
   for (int i = 0; i < fileInfo.Frame.GetLength(0); i++)
      frames.Add(i);
   byte [] buffer = engine.ExtractFramesMemory(inputStream, frames);
   //write buffer to the output stream
   outputStream.Write(buffer, 0, buffer.GetLength(0));
   //add all GML data
   engine.AppendGmlData(outputStream, gml);
}
